import './App.css';
   //import conn from './Conn.js';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUser } from '@fortawesome/free-solid-svg-icons'
import { faEnvelope } from '@fortawesome/free-solid-svg-icons'
import { faMobile } from '@fortawesome/free-solid-svg-icons'
import { faKey } from '@fortawesome/free-solid-svg-icons'
import { faUserEdit } from '@fortawesome/free-solid-svg-icons'
import axios from 'axios';
import { useEffect, useState } from 'react';

const api = axios.create({
  baseURL : "http://localhost:5000/"
});

function App() {

  const [name,setName] = useState('');
  const [email,setEmail] = useState('');
  const [phone,setPhone] = useState('');
  const [password,setPassword] = useState('');
  const [list,setList] = useState([]);

  const fetchData = async ()=>{
    try{
        const response = await api.get("/");
        console.log(response.data.data.list);
        setList(response.data.data.list);
    }catch(err){}
};
  const handleSubmit = async (e)=>{
    e.preventDefault();
    try{
      console.log(name);
      console.log(email);
      console.log(phone);
      console.log(password);

      const response =  await api.post("/create", {
        name
      }
      );
      console.log(response);
      setName("");
      fetchData();
    }catch(err){}
  };

  useEffect(() => {
    fetchData();
  }, [])

  return (
    <div className="App">



<center>
    <form action="" method="POST" >

    <table id="bod">
      <tr>
      <h1>Sign Up <FontAwesomeIcon icon={faUserEdit} /></h1>
      </tr>
    <tr><td><label id="lab">Name </label></td> <td><div className="tb"><i><FontAwesomeIcon icon={faUser} /></i><input id="tbox" value={name} onChange={(e)=>setName(e.target.value)}  placeholder="Enter Name" type="text"></input></div></td></tr>
    <tr><td><label id="lab">Email Id </label></td> <td><div className="tb"><i><FontAwesomeIcon icon={faEnvelope} /></i><input id="tbox" value={email} onChange={(e)=>setEmail(e.target.value)} placeholder="Enter Email id" type="text"></input></div></td></tr>
    <tr><td><label id="lab">Mobile No </label></td> <td><div className="tb"><i><FontAwesomeIcon icon={faMobile} /></i><input id="tbox" placeholder="Enter Mobile no." type="number"></input></div></td></tr>
    <tr><td><label id="lab">Password </label></td> <td><div className="tb"><i><FontAwesomeIcon icon={faKey} /></i><input id="tbox" placeholder="Enter Password" type="password"></input></div></td></tr>
    <tr></tr></table>
    <button className="button" onClick = {handleSubmit}><span>Submit </span></button>
    </form>
    </center>  








        {list &&

            list.map((l) => {
              return(
              <div key={ list.id }>

                   <h3>{ l.name }&nbsp;{ l.completed === true ? 'Done' : 'Not Done' }</h3>

              </div>
              )
            })

        }

    </div>
  );
}

export default App;
