import './App.css';
   //import conn from './Conn.js';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUser } from '@fortawesome/free-solid-svg-icons'
import { faEnvelope } from '@fortawesome/free-solid-svg-icons'
import { faMobile } from '@fortawesome/free-solid-svg-icons'
import { faKey } from '@fortawesome/free-solid-svg-icons'
import { faUserEdit } from '@fortawesome/free-solid-svg-icons'
import axios from 'axios';
import { useEffect, useState } from 'react';

const api = axios.create({
  baseURL : "http://localhost:5000/"
});

function App() {

  const [name,setName] = useState('');
  const [list,setList] = useState([]);

  const fetchData = async ()=>{
    try{
        const response = await api.get("/");
        console.log(response.data.data.list);
        setList(response.data.data.list);
    }catch(err){}
};
  const handleSubmit = async (e)=>{
    e.preventDefault();
    try{
      console.log(name);
      const response =  await api.post("/create", {
        name
      }
      );
      console.log(response);
      setName("");
      fetchData();
    }catch(err){}
  };

  useEffect(() => {
    fetchData();
  }, [])

  return (
    <div className="App">



<center>
    <form action="" method="POST" >

    <table id="bod">
      <tr>
      <h1>Sign Up <FontAwesomeIcon icon={faUserEdit} /></h1>
      </tr>
    <tr><td><label id="lab">Name </label></td> <td><div className="tb"><i><FontAwesomeIcon icon={faUser} /></i><input id="tbox" placeholder="Enter Name" type="text"></input></div></td></tr>
    <tr><td><label id="lab">Email Id </label></td> <td><div className="tb"><i><FontAwesomeIcon icon={faEnvelope} /></i><input id="tbox" placeholder="Enter Email id" type="text"></input></div></td></tr>
    <tr><td><label id="lab">Mobile No </label></td> <td><div className="tb"><i><FontAwesomeIcon icon={faMobile} /></i><input id="tbox" placeholder="Enter Mobile no." type="number"></input></div></td></tr>
    <tr><td><label id="lab">Password </label></td> <td><div className="tb"><i><FontAwesomeIcon icon={faKey} /></i><input id="tbox" placeholder="Enter Password" type="password"></input></div></td></tr>
    <tr></tr></table>
    <button className="button" onClick = {handleSubmit}><span>Submit </span></button>
    </form>
    </center>  






      <header className="App-header">
        <p>
         Task Manager - PERN demo
        </p>
        <form action="">
         <div>
          <input
              type="text"
              className="form-control"
              placeholder="Name"
              value = { name }
              onChange = {(e)=>setName(e.target.value)}
          />
          <button
            type="submit"
            className="btn btn-primary"
            onClick = {handleSubmit}
          >
            Add
          </button>
          </div> 
        </form>

        {list &&

            list.map((l) => {
              return(
              <div key={ list.id }>

                   <h3>{ l.name }&nbsp;{ l.completed === true ? 'Done' : 'Not Done' }</h3>

              </div>
              )
            })

        }
      </header>
    </div>
  );
}

export default App;
